// src/components/Cart/CartItem.js
import React from 'react';
import { useCart } from '../../context/CartContext';
import { formatCurrency } from '../../utils/formatters';

function CartItem({ item }) {
    const { removeFromCart } = useCart();
    
    // FIX: Add a check to ensure the item and its price/quantity exist
    if (!item || !item.price || !item.quantity) {
        return null; // Don't render if data is incomplete
    }

    const itemTotal = item.price * item.quantity;

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', borderBottom: '1px solid #eee', padding: '10px 0' }}>
            {/* Ensure item.name exists */}
            <span style={{ flex: 2 }}>{item.name || 'Unknown Product'}</span> 
            <span style={{ flex: 1 }}>Qty: {item.quantity}</span>
            <span style={{ flex: 1 }}>{formatCurrency(itemTotal)}</span> 
            <button onClick={() => removeFromCart(item.id)} style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '5px 10px', cursor: 'pointer' }}>
                Remove
            </button>
        </div>
    );
}

export default CartItem;
