// src/pages/Cart.js
// ... (imports remain the same) ...
// ... (formatCurrency import remains the same) ...

function Cart() {
    const { cartItems, getTotal } = useCart();

    if (cartItems.length === 0) {
        return <h2 style={{ padding: '20px', border: '1px dashed #ccc' }}>Your shopping cart is empty. Start adding some products!</h2>;
    }

    return (
        <div style={{ padding: '20px', border: '2px solid #333', borderRadius: '8px' }}> {/* Outer Border Added */}
            <h2>Your Shopping Cart</h2>
            <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px' }}>
                
                {/* Header Row */}
                <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    fontWeight: 'bold', 
                    borderBottom: '2px solid #333', 
                    paddingBottom: '10px', 
                    marginBottom: '10px' 
                }}>
                    <span style={{ flex: 2 }}>Item</span>
                    <span style={{ flex: 1 }}>Quantity</span>
                    <span style={{ flex: 1 }}>Price</span>
                    <span style={{ flex: 0.5 }}></span>
                </div>

                {/* List Cart Items */}
                {cartItems.map(item => (
                    <CartItem key={item.id} item={item} /> 
                ))}
            </div>

            {/* Total and Checkout Link remains the same */}
            <h3 style={{ marginTop: '30px', textAlign: 'right', paddingRight: '15px' }}>
                Grand Total: {formatCurrency(getTotal())}
            </h3>
            
            <Link 
                to="/checkout" 
                style={{ 
                    display: 'block', 
                    padding: '12px 20px', 
                    backgroundColor: 'darkgreen', 
                    color: 'white', 
                    textDecoration: 'none', 
                    marginTop: '15px', 
                    textAlign: 'center',
                    borderRadius: '5px'
                }}
            >
                Proceed to Checkout ({formatCurrency(getTotal())})
            </Link>
        </div>
    );
}

export default Cart;
