// src/components/Cart/CartItem.js
import React from 'react';
import { useCart } from '../../context/CartContext';
import { formatCurrency } from '../../utils/formatters';

function CartItem({ item }) {
    // Access context actions/data for quantity control
    const { removeFromCart, addToCart, decreaseQuantity } = useCart();
    
    // Safety check: Don't render if the item data is incomplete
    if (!item || !item.price || !item.quantity) {
        return null; 
    }

    const itemTotal = item.price * item.quantity;

    return (
        <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            borderBottom: '1px dashed #ddd', 
            padding: '10px 0',
            gap: '10px'
        }}>
            {/* 1. Product Name */}
            <span style={{ flex: 2, fontWeight: '500' }}>
                {item.name || 'Unknown Product'} 
            </span>
            
            {/* 2. Quantity Control */}
            <span style={{ flex: 1, display: 'flex', alignItems: 'center', gap: '5px' }}>
                {/* Decrease Quantity Button */}
                <button 
                    onClick={() => decreaseQuantity(item.id)} 
                    style={{ padding: '3px 8px', border: '1px solid #ccc', cursor: 'pointer' }}
                >
                    -
                </button>
                {/* Current Quantity */}
                <span style={{ minWidth: '20px', textAlign: 'center' }}>{item.quantity}</span>
                {/* Increase Quantity Button */}
                <button 
                    onClick={() => addToCart(item.id)} 
                    style={{ padding: '3px 8px', border: '1px solid #ccc', cursor: 'pointer' }}
                >
                    +
                </button>
            </span>

            {/* 3. Total Price for this Line Item (Formatted in INR) */}
            <span style={{ flex: 1, fontWeight: 'bold' }}>
                {formatCurrency(itemTotal)}
            </span> 
            
            {/* 4. Remove Button */}
            <button 
                onClick={() => removeFromCart(item.id)} 
                style={{ backgroundColor: 'darkred', color: 'white', border: 'none', padding: '5px 10px', cursor: 'pointer', borderRadius: '3px' }}
            >
                Remove
            </button>
        </div>
    );
}

export default CartItem;
