// src/pages/Checkout.js
import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { validateForm } from '../utils/validators';
import { useNavigate } from 'react-router-dom';

function Checkout() {
    const { cartItems, getTotal, clearCart } = useCart();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        address: ''
    });
    const [errors, setErrors] = useState({});
    const [isSubmitted, setIsSubmitted] = useState(false);

    // Don't allow checkout if cart is empty
    if (cartItems.length === 0 && !isSubmitted) {
        return <h2>Your cart is empty. Please add items to checkout.</h2>;
    }

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        // Clear error as user types
        setErrors({ ...errors, [e.target.name]: '' });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const validationErrors = validateForm(formData);
        setErrors(validationErrors);

        // If no errors, proceed with simulation
        if (Object.keys(validationErrors).length === 0) {
            // 1. Log or "send" the order data (simulation)
            console.log('Order Submitted:', {
                ...formData,
                items: cartItems,
                total: getTotal()
            });

            // 2. Clear the cart (key part of the simulation)
            clearCart();
            
            // 3. Update UI to show success
            setIsSubmitted(true);
        }
    };

    // Show confirmation message after successful submission
    if (isSubmitted) {
        return (
            <div style={{ padding: '40px', backgroundColor: '#e9ffe9', border: '1px solid #00a000' }}>
                <h2>ðŸŽ‰ Order Placed Successfully!</h2>
                <p>Thank you for your simulated purchase, **{formData.name}**!</p>
                <p>Your total charged was **${getTotal()}**.</p>
                <button onClick={() => navigate('/')}>Continue Shopping</button>
            </div>
        );
    }

    return (
        <div>
            <h2>Checkout Details</h2>
            <h3>Order Total: ${getTotal()}</h3>
            
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px', width: '400px', marginTop: '20px' }}>
                
                {/* Name Field */}
                <input
                    type="text"
                    name="name"
                    placeholder="Full Name"
                    value={formData.name}
                    onChange={handleChange}
                    style={{ padding: '10px', border: errors.name ? '1px solid red' : '1px solid #ccc' }}
                />
                {errors.name && <p style={{ color: 'red', margin: '0' }}>{errors.name}</p>}

                {/* Email Field */}
                <input
                    type="email"
                    name="email"
                    placeholder="Email Address"
                    value={formData.email}
                    onChange={handleChange}
                    style={{ padding: '10px', border: errors.email ? '1px solid red' : '1px solid #ccc' }}
                />
                {errors.email && <p style={{ color: 'red', margin: '0' }}>{errors.email}</p>}

                {/* Address Field */}
                <textarea
                    name="address"
                    placeholder="Shipping Address"
                    value={formData.address}
                    onChange={handleChange}
                    rows="3"
                    style={{ padding: '10px', border: errors.address ? '1px solid red' : '1px solid #ccc' }}
                ></textarea>
                {errors.address && <p style={{ color: 'red', margin: '0' }}>{errors.address}</p>}
                
                {/* Checkout Button */}
                <button type="submit" style={{ padding: '10px 20px', backgroundColor: 'darkblue', color: 'white', border: 'none', cursor: 'pointer' }}>
                    Place Simulated Order
                </button>
            </form>
        </div>
    );
}

export default Checkout;
