    const { cartItems, getTotal, clearCart } = useCart();
    // ... (state and functions remain the same) ...
    
    // ... (if (cartItems.length === 0) check remains the same) ...

    // Show confirmation message after successful submission
    if (isSubmitted) {
        return (
            <div style={{ 
                padding: '40px', 
                backgroundColor: '#e9ffe9', 
                border: '3px solid #00a000', // Stronger Success Border
                borderRadius: '8px', 
                margin: '20px auto', 
                width: '80%' 
            }}>
                <h2>ðŸŽ‰ Order Placed Successfully!</h2>
                {/* ... (confirmation text remains the same) ... */}
                <button onClick={() => navigate('/')} style={{ padding: '10px 20px', backgroundColor: 'darkblue', color: 'white', border: 'none', cursor: 'pointer', marginTop: '15px', borderRadius: '3px' }}>
                    Continue Shopping
                </button>
            </div>
        );
    }

    // Checkout Form View
    return (
        <div style={{ padding: '20px' }}>
            <h2>Checkout Details</h2>
            <h3>Order Total: {formatCurrency(finalTotal)}</h3>
            
            <form onSubmit={handleSubmit} style={{ 
                display: 'flex', 
                flexDirection: 'column', 
                gap: '15px', 
                width: '400px', 
                marginTop: '20px',
                padding: '20px',
                border: '1px solid #ccc', // Added Border to Form
                borderRadius: '5px'
            }}>
                
                {/* Form Fields with Validation Feedback (map loop remains the same) */}
                {['name', 'email', 'address'].map(field => (
                    <div key={field}>
                        <input
                            // ... (input properties remain the same) ...
                        />
                        {errors[field] && <p style={{ color: 'red', margin: '5px 0 0 0', fontSize: '0.8em' }}>{errors[field]}</p>}
                    </div>
                ))}
                
                <button type="submit" style={{ padding: '12px 20px', backgroundColor: 'darkorange', color: 'white', border: 'none', cursor: 'pointer', marginTop: '10px', borderRadius: '3px' }}>
                    Place Simulated Order
                </button>
            </form>
        </div>
    );
}

export default Checkout;
