// src/components/Products/ProductCard.js
import React from 'react';
import { useCart } from '../../context/CartContext'; // Get the cart functions

function ProductCard({ product }) {
    const { addToCart } = useCart();

    return (
        <div style={{ border: '1px solid #ccc', padding: '15px', margin: '10px', width: '250px' }}>
            {/* Replace with a real image if you have one in /assets/images */}
            <img src={product.imageUrl} alt={product.name} style={{ width: '100%', height: '150px', objectFit: 'cover' }} />
            <h3>{product.name}</h3>
            <p>${product.price.toFixed(2)}</p>
            <p style={{ fontSize: '0.9em' }}>{product.description}</p>
            <button onClick={() => addToCart(product.id)} style={{ padding: '8px 15px', backgroundColor: 'green', color: 'white', border: 'none', cursor: 'pointer' }}>
                Add to Cart
            </button>
        </div>
    );
}

export default ProductCard;
