// src/context/CartContext.js
import React, { createContext, useState, useContext } from 'react';
// Make sure this path is correct and products.js is updated with INR prices
import { products } from '../data/products'; 

// 1. Create the Context
const CartContext = createContext();

// Hook for easy access to cart context
export const useCart = () => useContext(CartContext); 

// 2. Create the Provider Component
export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);

    // Action 1: Add or Increase Quantity
    const addToCart = (productId) => {
        const existingItem = cartItems.find(item => item.id === productId);

        if (existingItem) {
            // Item exists, increase quantity
            setCartItems(cartItems.map(item =>
                item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
            ));
        } else {
            // Item is new, add it to the cart with quantity 1
            const productToAdd = products.find(p => p.id === productId);
            if (productToAdd) {
                // CRITICAL FIX: Ensure ALL product data is included in the new cart item
                setCartItems([...cartItems, { ...productToAdd, quantity: 1 }]); 
            }
        }
    };

    // Action 2: Remove item completely
    const removeFromCart = (productId) => {
        setCartItems(cartItems.filter(item => item.id !== productId));
    };

    // Action 3: Decrease Quantity
    const decreaseQuantity = (productId) => {
        setCartItems(cartItems => 
            cartItems.map(item => {
                if (item.id === productId) {
                    // Only decrease if quantity is greater than 1
                    if (item.quantity > 1) {
                        return { ...item, quantity: item.quantity - 1 };
                    }
                }
                return item;
            })
            // Filter out items where quantity is 1 and the user clicked remove
            .filter(item => item.quantity > 0) 
        );
    };

    // Action 4: Clear all items (for checkout simulation)
    const clearCart = () => setCartItems([]);

    // Utility: Calculate the total price
    const getTotal = () => {
        // Returns raw number for INR formatting in components
        return cartItems.reduce((total, item) => {
             // Safety check: ensure item.price exists before calculating
            const price = item.price || 0; 
            return total + price * (item.quantity || 0);
        }, 0); 
    };

    return (
        <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart, getTotal, decreaseQuantity }}>
            {children}
        </CartContext.Provider>
    );
};
