// src/pages/Cart.js
import React from 'react';
import { useCart } from '../context/CartContext';
import CartItem from '../components/Cart/CartItem';
import { Link } from 'react-router-dom';
import { formatCurrency } from '../utils/formatters'; // <-- NEW IMPORT

function Cart() {
    const { cartItems, getTotal } = useCart();

    if (cartItems.length === 0) {
        return <h2>Your shopping cart is empty. Start adding some products!</h2>;
    }

    return (
        <div style={{ padding: '20px' }}>
            <h2>Your Shopping Cart</h2>
            <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px' }}>
                
                {/* Header Row */}
                <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', borderBottom: '2px solid #333', paddingBottom: '10px', marginBottom: '10px' }}>
                    <span style={{ flex: 2 }}>Item</span>
                    <span style={{ flex: 1 }}>Quantity</span>
                    <span style={{ flex: 1 }}>Price</span>
                    <span style={{ flex: 0.5 }}></span> {/* For Remove Button */}
                </div>

                {/* List Cart Items */}
                {cartItems.map(item => (
                    // CartItem component will use formatCurrency internally for line totals
                    <CartItem key={item.id} item={item} /> 
                ))}
            </div>

            <h3 style={{ marginTop: '30px', textAlign: 'right', paddingRight: '15px' }}>
                Grand Total: {formatCurrency(getTotal())} {/* USE FORMATTER HERE */}
            </h3>
            
            <Link 
                to="/checkout" 
                style={{ 
                    display: 'block', 
                    padding: '12px 20px', 
                    backgroundColor: 'darkgreen', 
                    color: 'white', 
                    textDecoration: 'none', 
                    marginTop: '15px', 
                    textAlign: 'center',
                    borderRadius: '5px'
                }}
            >
                Proceed to Checkout ({formatCurrency(getTotal())})
            </Link>
        </div>
    );
}

export default Cart;
